import {LOGIN_ACTION, SIGNUP_ACTION} from "@/store/storeconstants";
import axios from "axios";
import ValidationsService from "@/services/ValidationsService";

export default {
    async [LOGIN_ACTION](context, payload) {
        await axios.post(
            'http://127.0.0.1:8000/api/v1/login',
            payload,
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                }
            }
        ).then(response => {
            if (response.status === 200) {
                context.commit('SET_USER_DATA_MUTATION', response.data);
            }
        }).catch(error => {
            let errorMessage = ValidationsService.getErrors(error.response.data.errors)
            throw errorMessage;
        });
    },
    async [SIGNUP_ACTION](context, payload) {
        await axios.post(
            'http://127.0.0.1:8000/api/v1/register',
            payload,
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                }
            }
        ).then(response => {
            if (response.status === 200) {
                context.commit('SET_USER_DATA_MUTATION', response.data);
            }else{
                try{
                    console.log(response)
                }catch (e) {
                    let errorMessage = ValidationsService.getErrors(response)
                    throw errorMessage;
                }

            }
        }).catch(error => {
            let errorMessage = ValidationsService.getErrors(error.response.data.errors)
            throw errorMessage;
        });
    }
};
